mapper.insert:
/* insertBoard */
INSERT INTO hjjboard (
	title,content,reg_date,status,category
) VALUES (
	  #{title}
	, #{content}
	, sysdate()
	, 'C'
	, #{category}
)

mapper.select:
/* selectBoardDetail */
SELECT 	title,content,reg_date,mod_date,seq,status,view_cnt,img_file
FROM 	hjjboard
WHERE	status in ('C', 'R')
and		seq = #{seq}

mapper.update:

/* updateBoard */
UPDATE	hjjboard
SET 	title = #{title}
		, content = #{content}
		, status = 'R'
		, mod_date = sysdate()
WHERE	seq = #{seq}
AND		status <> 'D'


/* deleteBoard */
UPDATE	hjjboard
SET		status = 'D'
		, mod_date = sysdate()
WHERE	seq = #{seq}
AND		status <> 'D'

mapper.select:
/* selectBoardList */ SELECT hj.title,hj.reg_date,hj.seq,hj.img_file , (SELECT count(*) FROM hjjcomment hc WHERE hc.board_seq = hj.seq and hc.status in ('C', 'R')) as comment_cnt FROM hjjboard hj WHERE hj.status in ('C', 'R')
AND hj.category = #{category}
ORDER BY seq DESC, reg_date DESC


/* selectBoardTotalCount */
SELECT	COUNT(*)
FROM	hjjboard
WHERE	status in ('C', 'R')

AND		category = #{category}

mapper.update:
/* updateViewCnt */
UPDATE	hjjboard
SET		view_cnt = view_cnt + 1
WHERE	1=1
and		seq = #{seq}

mapper.insert:
/* insertIP */
INSERT INTO ip_view_log (
	ip, reg_date, board_seq
) VALUES (
	  #{ip}
	, sysdate()
	, #{seq}
)

mapper.update:
/* updateIP */
UPDATE	ip_view_log
SET		reg_date = sysdate()
WHERE	ip = #{ip}
AND		board_seq = #{seq}

mapper.select:

/* checkIP */
SELECT	count(*)
FROM	ip_view_log
WHERE	ip = #{ip}
AND		board_seq = #{seq}


/* todayIP */
SELECT	count(*)
FROM	ip_view_log
WHERE	ip = #{ip}
AND		board_seq = #{seq}
AND		DATE(reg_date) = DATE(NOW())


/* viewToday */
SELECT	count(*)
FROM	ip_view_log
WHERE	board_seq = #{seq}
AND		DATE(reg_date) = DATE(NOW())
