mapper.select:
/* findByUserEmailAndUserPw */
SELECT 	count(*)
FROM 	hjjuser
WHERE	email = #{email}
AND		auth = #{auth}
AND		status = 'T'

		/* findByUserEmail */
		SELECT	*
		FROM	hjjuser
        WHERE	email = #{email}
        AND		status in ('C', 'R', 'T')

mapper.insert:
/* createUser */
INSERT INTO hjjuser (
	name,email,password,nickname,reg_date,isonline,istemppw,auth,status
) VALUES (
	  #{name}
	, #{email}
	, #{password}
	, ''
	, sysdate()
	, ''
	, ''
	, ''
	, 'T'
)

mapper.update:
/* updateUserStatus */
UPDATE 	hjjuser
SET status = 'C'
, 	role = 'ROLE_USER'
WHERE	email = #{email}
AND		status = 'T'

/* updateUserStatus */
UPDATE 	hjjuser
SET 	auth = #{auth}
WHERE	email = #{email}
